import Tapbar from "../components/Tapbar/Tapbar";
import Tips from "../components/Tips/Tips";
import Title from "../components/Title/Title";
import Button from "../components/Button/Button";
import Grid from "../components/Grid/Grid";
import Navbar from "../components/Navbar/Navbar";
import RewardCounter from "../components/Reward-counter/Reward-counter";
import Overlay from "../components/Overlay/Overlay"; // ✅ імпортуємо

import { useState } from "react";

function Game() {
    const [scope, setScope] = useState(0);
    const [xtwo, setXtwo] = useState(0);
    const [selectedCards, setSelectedCards] = useState([]);
    const [overlayCard, setOverlayCard] = useState(null); // ✅ новий стан

    const WorkCards = (card) => {
        switch (card.type) {
            case "cash":
                setOverlayCard(card); // ✅ викликаємо Overlay для cash
                break;

            case "bomb":
                setOverlayCard(card); // ✅ викликаємо Overlay для bomb
                break;

            case "xtwo":
                setXtwo(1);
                setScope(prev => prev * 2);
                break;

            case "zero":
                break;

            case "five":
                if (xtwo === 1) {
                    setScope(prev => prev + card.value * 2);
                    setXtwo(0);
                } else {
                    setScope(prev => prev + card.value);
                }
                break;

            default:
                break;
        }
    };

    const handleCardReveal = (card, index) => {
        setSelectedCards(prev => [...prev, { ...card, index }]);
        WorkCards(card);
    };

    return (
        <>
            <Navbar />
            <Title />
            <RewardCounter scope={scope} />
            <Grid onCardReveal={handleCardReveal} />
            <Tips />
            <Button />
            <Tapbar />

            {/* ✅ Показуємо Overlay, якщо є карта */}
            {overlayCard && <Overlay card={overlayCard} scope={scope} />}
        </>
    );
}

export default Game;
