        }
    }

    const handleCardReveal = (card, index) => {
        if (!gameStarted) {
            setGameStarted(true);
        }

        setSelectedCards(prev => [...prev, { ...card, index }]);
        WorkCards(card);
    };

    return (
        <>
            <Navbar />
            <Title />
            <RewardCounter scope={scope} xtwoState={xtwoState} />
            <Grid onCardReveal={handleCardReveal} resetTrigger={resetTrigger} />
            <Tips five={fiveNumber} xtwo={xtwoNumber} zero={zeroNumber} bomb={bombNumber} cash={cashNumber} />
            <Button
                isActive={gameStarted}
                onClick={gameStarted ? resetGame : null}
            />
            <Tapbar />
            {overlayCard && (
                <Overlay
                    card={overlayCard}
                    scope={scope}
                    onClose={() => setOverlayCard(null)}
                    resetGame={resetGameAllClean}
                />
            )}
        </>
    );
}

export default Game;
