import styles from "./Grid.module.css"
import React, {useState, useEffect} from "react";
import image_light from "../../assets/images/light.png";
import icons_cash from "../../assets/images/cash.png";
import image_bomb from "../../assets/images/bomb.png";
import image_stop from "../../assets/images/stop.png"

function Grid(){
    const [carts, setCarts] = useState([])
    const [revealed, setRevealed] = useState(Array(9).fill(false))
    const [five, setFive] = useState(5);
    const [cash, setCash] = useState(1);
    const [bomb, setBomb] = useState(1);
    const [xtwo, setXtwo] = useState(1);
    const [zero, setZero] = useState(1);

    useEffect(() => {
        const allCarts = [
            ...Array(five).fill('five'),
            ...Array(cash).fill('cash'),
            ...Array(bomb).fill('bomb'),
            ...Array(xtwo).fill('xtwo'),
            ...Array(zero).fill('zero'),
        ]

        const shuffledCart = allCarts.sort(() => Math.random() - 0.5)
        setCarts(shuffledCart);
    }, [])

    const handlerClick = (index) => {
        setRevealed((prev) => {
            const newRevealed = [...prev];
            newRevealed[index] = true;
            return newRevealed;
        })
    }

    const cartImage = {

    };

    {/*revealed[index] ? card : '$'*/}

    return (
        <div className={styles.Carts}>
            <div className={styles.container}>
                <ul className={styles.list}>
                    {carts.map((card, index) => (
                        <li
                            className={`${styles.item} ${styles[card]}`}
                            key={index}
                            onClick={() => handlerClick(index)}
                        >
                            {(card === 'fire' || card === 'cash' || card === 'bomb') && (
                                <>
                                    <div className={styles.itemData}>
                                        <img
                                            className={styles.icon}
                                            src={icons_cash}
                                            alt="cash"
                                        />
                                        <h1 className={styles.number}>
                                            100
                                        </h1>
                                    </div>
                                    <div
                                        className={styles.imageLight}
                                        style={{ backgroundImage: `url(${image_light})` }}
                                    ></div>
                                </>
                            )}
                            {(card === 'xtwo') && (
                                <>
                                    <div className={styles.itemData}>
                                        X2
                                    </div>
                                    <div
                                        className={styles.imageLight}
                                        style={{ backgroundImage: `url(${image_light})` }}
                                    ></div>
                                </>
                            )}
                            {(card === 'zero') && (
                                <>
                                    <div className={styles.itemData}>
                                        0
                                    </div>
                                    <div
                                        className={styles.imageLight}
                                        style={{ backgroundImage: `url(${image_light})` }}
                                    ></div>
                                </>
                            )}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Grid;
