import React, { useState, useEffect } from "react";
import styles from "./Grid.module.css";

import image_light from "../../assets/images/light.png";
import image_cash from "../../assets/images/cash.png";
import image_bomb from "../../assets/images/bomb.png";
import image_five from "../../assets/images/five.png";

function Grid() {
    const [carts, setCarts] = useState([]);
    const [revealed, setRevealed] = useState(Array(9).fill(false));

    const five = 5;
    const cash = 1;
    const bomb = 1;
    const xtwo = 1;
    const zero = 1;

    useEffect(() => {
        const fiveValues = [100, 500, 1000, 10000];
        const fiveCards = Array(five)
            .fill(null)
            .map(() => ({
                type: "five",
                value: fiveValues[Math.floor(Math.random() * fiveValues.length)],
            }));

        const allCarts = [
            ...fiveCards,
            ...Array(cash).fill({ type: "cash" }),
            ...Array(bomb).fill({ type: "bomb" }),
            ...Array(xtwo).fill({ type: "xtwo" }),
            ...Array(zero).fill({ type: "zero" }),
        ];

        const shuffled = [...allCarts].sort(() => Math.random() - 0.5);
        setCarts(shuffled);
    }, []);

    const handleClick = (index) => {
        setRevealed((prev) => {
            const updated = [...prev];
            updated[index] = true;
            return updated;
        });
    };

    const formatNumber = (num) => {
        return num >= 1000 ? `${num / 1000}K` : num;
    };

    return (
        <div className={styles.Carts}>
            <div className={styles.container}>
                <ul className={styles.list}>
                    {carts.map((card, index) => {
                        const isRevealed = revealed[index];

                        return (
                            <li
                                key={index}
                                className={`${styles.item} ${isRevealed ? styles[card.type] : ""}`}
                                onClick={() => handleClick(index)}
                            >
                                <div className={`${styles.flipCardInner} ${isRevealed ? styles.flipped : ""}`}>
                                    {/* Передня сторона */}
                                    <div className={styles.cardFront}>
                                        <div className={styles.cover}>$</div>
                                    </div>

                                    {/* Зворотня сторона */}
                                    <div className={`${styles.cardBack} ${styles.itemData}`}>
                                        {card.type === "five" && (
                                            <>
                                                <img
                                                    className={styles.icon}
                                                    src={image_five}
                                                    alt="five"
                                                />
                                                <h1 className={styles.number}>
                                                    {formatNumber(card.value)}
                                                </h1>
                                            </>
                                        )}
                                        {card.type === "bomb" && (
                                            <img
                                                className={styles.iconBomb}
                                                src={image_bomb}
                                                alt="bomb"
                                            />
                                        )}
                                        {card.type === "cash" && (
                                            <img
                                                className={styles.iconCash}
                                                src={image_cash}
                                                alt="cash"
                                            />
                                        )}
                                        {card.type === "xtwo" && (
                                            <h1 className={styles.number}>X2</h1>
                                        )}
                                        {card.type === "zero" && (
                                            <h1 className={styles.number}>0</h1>
                                        )}
                                    </div>
                                </div>

                                {isRevealed && (
                                    <div
                                        className={`
                                            ${styles.imageLight}
                                            ${styles.imageLightFive}
                                            ${styles.imageLightCash}
                                        `}
                                        style={{ backgroundImage: `url(${image_light})` }}
                                    ></div>
                                )}
                            </li>
                        );
                    })}
                </ul>
            </div>
        </div>
    );
}

export default Grid;
