import React, { useState, useEffect } from "react";
import styles from "./Grid.module.css";
import { motion, useAnimation } from "framer-motion";

import image_light from "../../assets/images/light.png";
import image_cash from "../../assets/images/cash.png";
import image_bomb from "../../assets/images/bomb.png";
import image_five from "../../assets/images/five.png";

function Grid() {
    const [carts, setCarts] = useState([]);
    const [revealed, setRevealed] = useState(Array(9).fill(false));
    const [showContent, setShowContent] = useState(Array(9).fill(false));

    const five = 5;
    const cash = 1;
    const bomb = 1;
    const xtwo = 1;
    const zero = 1;

    useEffect(() => {
        const fiveValues = [100, 500, 1000, 10000];
        const fiveCards = Array(five)
            .fill(null)
            .map(() => ({
                type: "five",
                value: fiveValues[Math.floor(Math.random() * fiveValues.length)],
            }));

        const allCarts = [
            ...fiveCards,
            ...Array(cash).fill({ type: "cash" }),
            ...Array(bomb).fill({ type: "bomb" }),
            ...Array(xtwo).fill({ type: "xtwo" }),
            ...Array(zero).fill({ type: "zero" }),
        ];

        const shuffled = [...allCarts].sort(() => Math.random() - 0.5);
        setCarts(shuffled);
    }, []);

    // Створюємо контролери анімації для кожної карточки
    const controlsArray = carts.map(() => useAnimation());

    const handleClick = async (index) => {
        if (revealed[index]) return; // якщо вже відкрито - нічого не робити

        setRevealed((prev) => {
            const updated = [...prev];
            updated[index] = true;
            return updated;
        });

        setShowContent((prev) => {
            const updated = [...prev];
            updated[index] = false; // поки не показуємо контент
            return updated;
        });

        // Запускаємо анімацію повороту
        await controlsArray[index].start({ rotateY: 180, transition: { duration: 0.6 } });

        // Після завершення анімації показуємо контент
        setShowContent((prev) => {
            const updated = [...prev];
            updated[index] = true;
            return updated;
        });
    };

    const formatNumber = (num) => {
        return num >= 1000 ? `${num / 1000}K` : num;
    };

    return (
        <div className={styles.Carts}>
            <div className={styles.container}>
                <ul className={styles.list}>
                    {carts.map((card, index) => {
                        const isRevealed = revealed[index];
                        const isContentVisible = showContent[index];

                        return (
                            <motion.li
                                key={index}
                                className={`${styles.item}`}
                                onClick={() => handleClick(index)}
                                animate={controlsArray[index]}
                                initial={{ rotateY: 0 }}
                                style={{ transformStyle: "preserve-3d" }}
                            >
                                <div
                                    className={styles.cardFace}
                                    style={{
                                        backfaceVisibility: "hidden",
                                        position: "absolute",
                                        width: "100%",
                                        height: "100%",
                                    }}
                                >
                                    <div className={styles.cover}>$</div>
                                </div>

                                <div
                                    className={styles.cardBack}
                                    style={{
                                        transform: "rotateY(180deg)",
                                        backfaceVisibility: "hidden",
                                        position: "absolute",
                                        width: "100%",
                                        height: "100%",
                                    }}
                                >
                                    {isContentVisible && (
                                        <div className={`${styles.itemData} ${styles[card.type]}`}>
                                            {card.type === "five" && (
                                                <>
                                                    <img
                                                        className={styles.icon}
                                                        src={image_five}
                                                        alt="five"
                                                    />
                                                    <h1 className={styles.number}>
                                                        {formatNumber(card.value)}
                                                    </h1>
                                                </>
                                            )}
                                            {card.type === "bomb" && (
                                                <img
                                                    className={styles.iconBomb}
                                                    src={image_bomb}
                                                    alt="bomb"
                                                />
                                            )}
                                            {card.type === "cash" && (
                                                <img
                                                    className={styles.iconCash}
                                                    src={image_cash}
                                                    alt="cash"
                                                />
                                            )}
                                            {card.type === "xtwo" && (
                                                <h1 className={styles.number}>X2</h1>
                                            )}
                                            {card.type === "zero" && (
                                                <h1 className={styles.number}>0</h1>
                                            )}
                                        </div>
                                    )}

                                    {isContentVisible && (
                                        <div
                                            className={`${styles.imageLight} ${styles.imageLightFive} ${styles.imageLightCash}`}
                                            style={{ backgroundImage: `url(${image_light})` }}
                                        ></div>
                                    )}
                                </div>
                            </motion.li>
                        );
                    })}
                </ul>
            </div>
        </div>
    );
}

export default Grid;
