import {useRef, useEffect} from "react";

import styles from "./Reward-conunter.module.css"
import icon_five from "../../assets/icons/cash.svg";

function RewardCounter({scope, xtwoState}) {
    const balanceRef = useRef(null);

    useEffect(() => {
        if (onBalanceRef && balanceRef.current) {
            onBalanceRef(balanceRef);
        }
    }, [onBalanceRef]);

    return (
        <div className={styles.RewardCounter}>
            <div className={styles.container}>
                {xtwoState && (
                    <div className={styles.xtwo}>x2</div>
                )}
                <img className={styles.img} src={icon_five} />
                <h1 className={styles.number} >{scope}</h1>
            </div>
        </div>
    )
}

export default RewardCounter;
