import React, { useState, useEffect } from "react";
import styles from "./Grid.module.css";

// Імпортуй свої зображення
import image_light from "../../assets/images/light.svg";
import image_cash from "../../assets/images/cash.png";
import image_bomb from "../../assets/images/bomb.png";
import image_five from "../../assets/images/five.png"

function Grid() {
    const [carts, setCarts] = useState([]);
    const [revealed, setRevealed] = useState(Array(9).fill(false));

    // Кількість кожного типу карт
    const five = 5;
    const cash = 1;
    const bomb = 1;
    const xtwo = 1;
    const zero = 1;

    useEffect(() => {
        const allCarts = [
            ...Array(five).fill("five"),
            ...Array(cash).fill("cash"),
            ...Array(bomb).fill("bomb"),
            ...Array(xtwo).fill("xtwo"),
            ...Array(zero).fill("zero"),
        ];

        const shuffled = [...allCarts].sort(() => Math.random() - 0.5);
        setCarts(shuffled);
    }, []);

    const handleClick = (index) => {
        setRevealed((prev) => {
            const updated = [...prev];
            updated[index] = true;
            return updated;
        });
    };

    return (
        <div className={styles.Carts}>
            <div className={styles.container}>
                <ul className={styles.list}>
                    {carts.map((card, index) => {
                        const isRevealed = revealed[index];

                        return (
                            <li
                                key={index}
                                className={`${styles.item} ${isRevealed ? styles[card] : ""}`}
                                onClick={() => handleClick(index)}
                            >
                                {isRevealed ? (
                                    <div className={styles.itemData}>
                                        {card === "five" && (
                                            <>
                                                <img
                                                    className={styles.icon}
                                                    src={image_five}
                                                    alt="five"
                                                />
                                                <h1 className={styles.number}>100</h1>
                                            </>
                                        )}
                                        {card === "bomb" && (
                                            <img
                                                className={styles.iconBomb}
                                                src={image_bomb}
                                                alt="bomb"
                                            />
                                        )}
                                        {card === "cash" && (
                                            <>
                                                <img
                                                    className={styles.iconCash}
                                                    src={image_cash}
                                                    alt="cash"
                                                />
                                            </>
                                        )}
                                        {card === "xtwo" && <h1 className={styles.number}>X2</h1>}
                                        {card === "zero" && <h1 className={styles.number}>0</h1>}
                                    </div>
                                ) : (
                                    <div className={styles.cover}>$</div>
                                )}
                                <div
                                    className={styles.imageLight}
                                    style={{ backgroundImage: `url(${image_light})`, color: 'red' }}
                                ></div>
                            </li>
                        );
                    })}
                </ul>
            </div>
        </div>
    );
}

export default Grid;
